imag.script:

            var fromXml = $param['fromXml'];
            var model = $param['model'];
            var info = $phone.info();
            var fujianwidth='';
            if(info['platform']=='android'){
              fujianwidth='width:200';
            }
        	var storage = $phone.localStorage();
        	var evoUrl = storage.getItem('evoUrl');
        	var	workStatus = storage.getItem('workflow_workStatus');
	        var workId = storage.getItem('workflow_workId');
	        var webServiceUrl = storage.getItem('webServiceUrl');
	        // 需要添加附件下载内容的id数组
			var idArrays = new Array();
			var picRealFiles;
	        var picSaveFiles;
			var appRealFiles;
	        var appSaveFiles;
	        var itemContent = '';
	        var draftPerson = '';  //发起人
        	var worktitle = '';  //流程标题
        	var worksubmittime = '';   //发起时间
        	var bodyIndex = 1 ;      //正文index
	        // 页面加载完后执行
        	$page.onload = function(){
				$http.progress('正在加载，请稍后...').post(
        			evoUrl + '/ezOffice/dealfile/infoProcess.do',{workId : workId},
        			function(data){
        				if(!data){
        					alert('数据获取失败！');
        				}
        				data = data.replace(/\&/g,'＆');
        				var jsonData = eval('(' + data + ')');
        				if(jsonData.logonerror && jsonData.logonerror=='1'){
		         			alert("该账号已在设备"+jsonData.deviceId+"上登录");
		          			$page.open('../index.xml',{target:'_self'});
		                    return;
		                }
        				var workInfoResult = jsonData.data_0.message.result;
        				var detailResult = jsonData.data_1.message.result;
        				if(workInfoResult != '1' || detailResult != '1'){
        					alert('数据获取失败！');
        				}
        				var workInfo = jsonData.data_0.data.workInfo;
						var detailData = jsonData.data_1.data;
						var commentList = jsonData.data_0.data.commentList;
						var $contentFormList = $('content_form_list');
						//创建流程图
       					createFlowImg(workInfo.flowgraphurl,jsonData.data_2.picRootPath);
       					$('headers').css('display','');
       					// 创建工作流程信息隐藏域
						createWorkInfoHiddenArea(workInfo);
       					// 初始化信息表单数据
       					createFormData(detailData);
       					//如果存在已批的意见则创建
       					if(commentList){
       						createCommList(commentList);
       					}
       					//如果存在办理意见则创建办理意见
       					if(workStatus=='0' || workStatus=='2'){
       						var officeList = jsonData.data_0.data.officelist;
       					    createCommentCon(workInfo,officeList);
       					}
       					$contentFormList.addMore('<imag><list>' + itemContent + '</list></imag>');
       					//创建编辑正文附件名称
						createBodyFileName(workInfo);
       					// 根据信息类型,设置信息内容
						setInfoContent(detailData,jsonData.data_1);	
						idArrays.map(function(id){
							createAttCon(id);
						});
       					// 创建底部内容
       					createFootContent(workInfo);
       					var backComment = workInfo.backComment;   //退回意见弹出
        				if(backComment!=null&&backComment!=''&&backComment!='[]'){
        				   alert(backComment.replace(/(<[^>]+>)|(&nbsp;)/g,""));
        				}
        			},function(error) {
				    if (error == 'timeout') {
						hint('连接服务器超时！');
				    } else if (error == '404') {
						hint('链接超时重新登录！');
				    } else if (error == '500') {
						hint('内部服务器错误！');
				    } else {
						hint('访问网络错误！错误代码:' + error);
				    }
				    $page.open('../index.xml',{animation:'down-to-up'});
				  }
        		);
        	}

        	function createBodyFileName(workInfo){
	        	draftPerson = workInfo.worksubmitperson == null ? '': workInfo.worksubmitperson;
	        	worktitle =  workInfo.worktitle== null ? '': workInfo.worktitle;
	        	worktitle = worktitle.replace('等待您的审阅！','');
	        	worktitle = worktitle.replace('等待您的审阅','');
	        	worktitle = worktitle.replace('等待您的办理！','');
	        	worktitle = worktitle.replace('等待您的办理','');
	        	worksubmittime = workInfo.worksubmittime == null ? '': workInfo.worksubmittime;
	        	if(worksubmittime != null && worksubmittime != '' && worksubmittime.length>10){
	        		worksubmittime = worksubmittime.substring(0,10).replace(/-/g,'');
	        	}
	        }

