			// 根据信息类型,设置信息内容 TODO
			function setInfoContent(detailData,informationData){
				var content;
				if(informationData.content!=""&&informationData.content!=null){
					content = informationData.content;
				}else{
					content = detailData.informationContent;
				}
				var $infoContent = $('infoContent');
				var informationType = detailData.informationType;
	        	var infoContent = '';
	        	if(informationType == '0' || informationType == '1'){
	        		infoContent = $C('<web scalable="true" style="color:#555555;margin:10 10;" id="content_web"></web>');
		        	$infoContent.add(infoContent);
		        	$('content_web').html = '<article class="wh-article" ><div class="wh-article-fulltext">' +content+ '</div></article>';
	        	}else if(informationType == '2'){
	        		// 地址连接
	        		infoContent = $C('<row style="margin-top:30;margin-bottem:30px;"><web id="content_web"></web></row>');
	        		$infoContent.add(infoContent);
	        		$('content_web').html = '<div style="text-align:center;" ><a href="" >'+content+'</a></div>';
	        	}else if(informationType == '3'){
	        		// 文件链接
	        		var cons = content.split(":");
	        		if(cons && cons.length==2){
	        			var reName = cons[0];
	        			var saName = cons[1];
	        			viewFiles2('infoContent',reName,saName,"information",reName.substring(reName.indexOf("."),reName.length));
	        		}
	        	}else if(informationType == '4'){
	        		//word
	        		viewFiles2('infoContent',"正文",content+".doc","information",".doc");
	        	}else if(informationType == '5'){
	        		// excel
	        		viewFiles2('infoContent',"正文",content+".xls","information",".xls");
	        	}else if(informationType == '6'){
	        		// ppt
	        		viewFiles2('infoContent',"正文",content+".ppt","information",".ppt");
	        	}
			}

			function downFile(accessorySaveNameseries,accessoryNameseries){
			   var path = 'html';
	            $http.post(evoUrl+'/ezOffice/doc/dealFiles.do',
	              {accessoryNameseries:accessoryNameseries,accessorySaveNameseries:accessorySaveNameseries,path:path},
	              function(data) {
		               if(data){
		                   var obj = JSON.parse(data);
		                   if(obj.logonerror &&obj.logonerror=='1'){
				     		    alert("该账号已在设备"+obj.deviceId+"上登录");
				     		    $page.open('../index.xml',{target:'_self'});
				                return;
				           }
			               var attList = obj.attList;
			               if(!attList==""){  //不为空，数据进行迭代,并为附件赋值
			                  for(var k=0;k<attList.length;k++){
			                 	$http.download(evoUrl+'/ezOffice/download/file.do?url='+attList[k].attachurlser+'&filename='+attList[k].savename+'&uploadPath=html'+'&viewName='+attList[k].realname);
			                  }
			               }else{     //无附件，或者后台处理异常

			               }
		               }

		            },function(error) {
					    if (error == 'timeout') {
						hint('连接服务器超时！');
					    } else if (error == '404') {
						hint('链接超时重新登录！');
					    } else if (error == '500') {
						hint('内部服务器错误！');
					    } else {
						hint('访问网络错误！错误代码:' + error);
				    }
				    $page.open('../index.xml',{animation:'down-to-up'});
				  });
            }

			function viewFiles2(filetype,name1,name2,path,fileType){
	            $http.post(evoUrl+'/ezOffice/doc/dealFiles.do',{accessoryNameseries:name1,accessorySaveNameseries:name2,path:path},  function(data) {
	               if(data){
	                   var obj = JSON.parse(data);
	                   if(obj.logonerror && obj.logonerror=='1'){
		         			alert("该账号已在设备"+obj.deviceId+"上登录");
		          			$page.open('../index.xml',{target:'_self'});
		                    return;
		               }
		               var attList = obj.attList;
		               if(!attList==""){  //不为空，数据进行迭代,并为附件赋值
		                  for(var k=0;k<attList.length;k++){
		                  	  var viewName = draftPerson +worksubmittime +worktitle+"的正文"+bodyIndex+fileType;
		                      bodyIndex++;
		                      var files = $C('<row><label style="color:#38adff;align:left;font-size:16;"  onclick="$http.download(\''+evoUrl+'/ezOffice/download/file.do?url='+attList[k].attachurlser+'&amp;filename='+attList[k].savename+'&amp;uploadPath=govdocumentmanager'+'&amp;viewName='+viewName+'\')">查看正文</label></row>')
		                      $(filetype).add(files); 
		                  }
		               }else{     //无附件，或者后台处理异常

		               }
	               }
	            },function(error) {
					    if (error == 'timeout') {
					        hint('连接服务器超时！');
					    } else if (error == '404') {
					        hint('页面不存在！');
					    } else if (error == '500') {
					        hint('内部服务器错误！');
					    } else {
					        hint('访问网络错误！错误代码:' + error);
					    }
					    $page.open('../index.xml',{animation:'down-to-up'});
				   }
	            );
        }


