	        // 构建附件内容
			function createAttCon(appendId){
				var attConent = '';
				var accessoryNameseries = '';
				var accessorySaveNameseries = '';
		        var realFiles;
		        var saveFiles;
		        if(appendId == 'picCol'){
		        	realFiles = picRealFiles;
		        	saveFiles = picSaveFiles;
		        }else if(appendId == 'appCol'){
		        	realFiles = appRealFiles;
		        	saveFiles = appSaveFiles;
		        }
				if(realFiles instanceof Array){
					for(var i=0,length=realFiles.length;i<length;i++){
						if(length-1 > i){
							accessoryNameseries += realFiles[i] + '|';
							accessorySaveNameseries += saveFiles[i] + '|';
						}else{
							accessoryNameseries += realFiles[i];
							accessorySaveNameseries += saveFiles[i];
						}
					}
				}else{
					accessoryNameseries = realFiles;
					accessorySaveNameseries = saveFiles;
				}
				// 创建下载附件内容
				viewFiles(accessoryNameseries,accessorySaveNameseries,'information',appendId);
			}

			// 创建下载附件内容
			function viewFiles(accessoryNameseries, accessorySaveNameseries, path, appendId) {
				$http.post(evoUrl + '/ezOffice/doc/dealFiles.do', {
					accessoryNameseries: accessoryNameseries,
					accessorySaveNameseries: accessorySaveNameseries,
					path: path
				}, function(data) {
					if (data) {
						var obj = JSON.parse(data);
						if(obj.logonerror && obj.logonerror=='1'){
		         			alert("该账号已在设备"+obj.deviceId+"上登录");
		          			$page.open('../index.xml',{target:'_self'});
		                    return;
		               }
						var attList = obj.attList;
						if (!attList == '') { //不为空，数据进行迭代,并为附件赋值
							for (var k = 0; k < attList.length; k++) {
								var files = $C('<row style="margin-bottom:5"><icon src="mail_fujian.png" style="align:left;width:16;height:16"></icon><label style="color:#38adff;text-decoration:underline;align:left;font-size:16;margin-left:5"  onclick="$http.download(\''+evoUrl+'/ezOffice/download/file.do?url=' + attList[k].attachurlser + '&amp;filename=' + attList[k].savename + '&amp;viewName=' + attList[k].realname +'\')">' + attList[k].realname + '</label></row>')
								$(appendId).add(files);
							}
						}
					}
				},function(error) {
				    if (error == 'timeout') {
						hint('连接服务器超时！');
				    } else if (error == '404') {
						hint('链接超时重新登录！');
				    } else if (error == '500') {
						hint('内部服务器错误！');
				    } else {
						hint('访问网络错误！错误代码:' + error);
				    }
				    $page.open('../index.xml',{animation:'down-to-up'});
				  });
			}

			function createCommList(commentList){
				var commentShouxie = '';
				var commentYuyin = '';
				if(commentList instanceof Array){
					for(var i=0,length=commentList.length;i<length;i++){
						step = commentList[i].step+":";
						itemContent +='<item style="col-width:90,*"><col><row><label style="font-size:16;color:#555555;">'+step+'</label>'+
	        				   '</row></col><col>';
						commentType = commentList[i].type;
						if(commentType == '4'){
							// 手写
							//commentShouxie = webServiceUrl + 'upload/workflow_acc/' + commentList[i].content.substring(0,6) + '/' + commentList[i].content;
							commentShouxieOaUrl = webServiceUrl + 'upload/workflow_acc/' + commentList[i].content.substring(0,6) + '/' + commentList[i].content;
							commentShouxie = evoUrl+'/ezOffice/download/resizeHandSign.do?url='+commentShouxieOaUrl;
							itemContent += '<row style="margin-bottom:20" onclick="view_sxContent(\'' + commentShouxie + '\');"><img src="'+commentShouxie+'"/></row>';
						}else if(commentType == '5'){
							// 语音
							commentYuyin = webServiceUrl + 'upload/workflow_acc/' + commentList[i].content.substring(0,6) + '/' + commentList[i].content;
							itemContent += '<row style="margin-bottom:20"><label onclick="playSound(\'' + commentYuyin + '\',\''+commentList[i].content+'\')" style="align:left;font-size:14;color:#38adff;">'+(commentList[i].content ? commentList[i].content : "")+'</label></row>';
						}else{
							itemContent += '<row style="margin-bottom:20"><label style="align:left;font-size:16;color:#555555">'+(commentList[i].content ? commentList[i].content : "")+'</label></row>';
						}
						itemContent += '<row><label style="align:right;color:#b8b8b8;font-size:12">'+commentList[i].person+'('+commentList[i].date+')</label></row></col></item>';
					}
				}else if(commentList instanceof Object){
					step = commentList.comment.step+":";
						itemContent +='<item style="col-width:90,*"><col><row><label style="font-size:16;color:#555555;">'+step+'</label>'+
	        				   '</row></col><col>';
					commentType = commentList.comment.type;
					if(commentType == '4'){
						// 手写
					//	commentShouxie = webServiceUrl + 'upload/workflow_acc/' + commentList.comment.content.substring(0,6) + '/' + commentList.comment.content;
						commentShouxieOaUrl = webServiceUrl + 'upload/workflow_acc/' + commentList.comment.content.substring(0,6) + '/' + commentList.comment.content;
						commentShouxie = evoUrl+'/ezOffice/download/resizeHandSign.do?url='+commentShouxieOaUrl;
						itemContent += '<row style="margin-bottom:20" onclick="view_sxContent(\'' + commentShouxie + '\');"><img src="'+commentShouxie+'"/></row>';
					}else if(commentType == '5'){
						// 语音
						commentYuyin = webServiceUrl + 'upload/workflow_acc/' + commentList.comment.content.substring(0,6) + '/' + commentList.comment.content;
						itemContent += '<row style="margin-bottom:20"><label onclick="playSound(\'' + commentYuyin + '\',\''+commentList.comment.content+'\')" style="align:left;font-size:14;color:#38adff">'+(commentList.comment.content ? commentList.comment.content : "")+'</label></row>';
					}else{
						itemContent += '<row style="margin-bottom:20"><label style="align:left;font-size:16;color:#555555">'+(commentList.comment.content ? commentList.comment.content : "")+'</label></row>';
					}
					itemContent += '<row><label style="align:right;color:#b8b8b8;font-size:12">'+commentList.comment.person+'('+commentList.comment.date+')</label></row></col></item>';
				}
			}
			//播放语音
			function playSound(path,filename){
			    commentYuyinUrl =  evoUrl+'/ezOffice/download/playSound.do?url='+path+'&filename='+filename;
			    $http.post(evoUrl + '/ezOffice/download/playSound.do', {
						url : path,
						filename : filename
					}, function(data) {
					   // alert(data);
						$phone.playSound(evoUrl+'/ezOffice/upload/'+filename); 
					},function(error) {
				    if (error == 'timeout') {
						hint('连接服务器超时！');
				    } else if (error == '404') {
						hint('链接超时重新登录！');
				    } else if (error == '500') {
						hint('内部服务器错误！');
				    } else {
						hint('访问网络错误！错误代码:' + error);
				    }
				    $page.open('../index.xml',{animation:'down-to-up'});
				  }
				);
			}

