			// 打开手写签批或语言签批菜单
			function openCommentMenu(){
				var $commentAdd = $('commentAdd');
				if($commentAdd.src == 'deskperson_add.png'){
					$('commentAdd').src = 'workflow_del.png';
					$('shouxie').css('display','');
					$('yuyin').css('display','');
				}else if($commentAdd.src == 'workflow_del.png'){
					$('commentAdd').src = 'deskperson_add.png';
					$('shouxie').css('display','none');
					$('yuyin').css('display','none');
				}
			}

			// 打开常用语菜单
	        function openUseComment(officeList) {
	        	if(!officeList){
	        		return false;
	        	}
	        	var officeArray = officeList.split(',');
	        	var actionMenuCon = '<actionmenu title="选择常用语" onclose="">';
	        	officeArray.map(function(office){
	        	    if(office != 'undefined'){
	        			actionMenuCon += '<item label="' + office + '" onclick="setCommentVal(\'' + office + '\')"/>';
	        		}
	        	});
        		actionMenuCon += '</actionmenu>';
	            var menu = $C(actionMenuCon);
	            menu.open();
	        }


	        // 批示意见内容,各环节办理节点意见
	        function createCommentCon(comment){
	        //	var isCurrent = comment.current;
	        	var commentCon = '<item style="col-width:100,*;padding:0;margin:0;"><col style="background:#f9f9f9;margin:10;padding:10;">';
	        	commentCon += '<row><label style="align:left;font-size:16;color:#555555">'+comment.step+':</label></row></col><col style="margin:10;padding:10;">';
	        	if(comment){
	        		if(comment instanceof Array){
	        			for(var i=0,length=commentContent.length;i<length;i++){
				        	commentCon += '<row><label style="align:right;color:#555555">'+comment[i].content + ' ' + comment[i].person + ' (' + comment[i].date +')</label></row>';
	        			}
	        		}else if(comment instanceof Object){
			        	commentCon += '<row><label style="align:right;color:#555555">'+comment.content + ' ' +comment.person + ' (' + comment.date +')</label></row>';
	        		}
	        	}
	        	commentCon += '</col></item>';
	        	return commentCon;
	        }

        	 //创建流程图
	        function createFlowImg(flowGraphUrl,picRootPath){
