	        // 打开转办 加签
	        function openPopMenu(modiButton){
	        	if(modiButton.indexOf('Tran') < 0){
		        	$('popupmenu_list').remove($('tranInfoItem'));
	        	}else if(modiButton.indexOf('AddSign') < 0){
