			// 构建附件内容
			function createAttCon(){
				var attConent = '';
				var accessoryNameseries = '';
				var accessorySaveNameseries = '';
				if(realFiles instanceof Array){
					for(var i=0,length=realFiles.length;i<length;i++){
						if(length-1 > i){
							accessoryNameseries += realFiles[i] + '|';
							accessorySaveNameseries += saveFiles[i] + '|';
						}else{
							accessoryNameseries += realFiles[i];
							accessorySaveNameseries += saveFiles[i];
						}
					}
				}else if(realFiles instanceof Object){
					accessoryNameseries = realFiles.file;
					accessorySaveNameseries = saveFiles.file;
				}
				// 创建下载附件内容
				if(accessoryNameseries && accessorySaveNameseries){
					viewFiles(accessoryNameseries,accessorySaveNameseries,'govdocumentmanager','attachments');
				}
			}

			// 构建pdf批注文件
			function creatPdf(){
				if(workStatus!='0' && workStatus!='2'){
					var pdfRealName='';
					var pdfSaveName='';
					if(pdfFileList instanceof Array){
						for(var i=0;i<pdfFileList.length;i++){
							var pdfsfpz = pdfFileList[i].pdfsfpz;
							var pdfrealname = pdfFileList[i].pdfrealname;
							var pdfsavename = pdfFileList[i].pdfsavename;
							if(pdfsfpz=='1'){
								if(pdfFileList.length-1 > i){
									pdfRealName += pdfrealname + '|';
									pdfSaveName += pdfsavename + '|';
								}else{
									pdfRealName += pdfrealname;
									pdfSaveName += pdfsavename;
								}
							}
						}
					}else if(pdfFileList instanceof Object){
						var pdfsfpz = pdfFileList.pdfdata.pdfsfpz;
						if(pdfsfpz=='1'){
							pdfRealName = pdfFileList.pdfdata.pdfrealname;
							pdfSaveName = pdfFileList.pdfdata.pdfsavename;
						}
					}
					if(pdfRealName && pdfSaveName){
						viewFiles(pdfRealName,pdfSaveName,'pdffile','pdfFile');
					}
				}
			}

			// 创建下载附件内容
			function viewFiles(accessoryNameseries, accessorySaveNameseries, path, appendId) {
				$http.post(evoUrl + '/ezOffice/doc/dealFiles.do', {
					accessoryNameseries: accessoryNameseries,
					accessorySaveNameseries: accessorySaveNameseries,
					path: path
				}, function(data) {
					if (data) {
						var obj = JSON.parse(data);
						if(obj.logonerror &&obj.logonerror=='1'){
			         		alert("该账号已在设备"+obj.deviceId+"上登录");
		          			$page.open('../index.xml',{target:'_self'});
		                    return;
		                }
						var attList = obj.attList;
						if (!attList == '') { //不为空，数据进行迭代,并为附件赋值
							for (var k = 0; k < attList.length; k++) {
								var files = $C('<row><label style="color:#330000;color:blue;"  onclick="$http.download(\'' + evoUrl + '/ezOffice/download/file.do?url=' + attList[k].attachurlser + '&amp;filename=' + attList[k].savename + '&amp;viewName=' + attList[k].realname +'\')">' + attList[k].realname + '</label></row>')
								$(appendId).add(files);
							}
						} else { //无附件，或者后台处理异常

						}
					}
				},function(error) {
				    if (error == 'timeout') {
						hint('连接服务器超时！');
				    } else if (error == '404') {
						hint('链接超时重新登录！');
				    } else if (error == '500') {
						hint('内部服务器错误！');
				    } else {
						hint('访问网络错误！错误代码:' + error);
				    }
				    $page.open('../index.xml',{animation:'down-to-up'});
				  });
			}

