        	function doPdf(){
        		if(pdflist+'' =='undefined' || pdflist+'' ==''){
        			alert("无可批注的有效文件！");
        			return;
        		}
        		if(pdflist instanceof Array){
        			$('pdfList').clear();
		            $('main_popupmenu').position='center';
		            $('main_popupmenu').style('display:block;width:360;height:260;border:1 white;background:white;');
		            $('pdfList').css('display', 'block');
		       	    $('popupmenu_list').css('display', 'none');
		            var fileTitle = $C('<list-item><item><row><label style="align:center">请选择批注版本</label></row></item></list-item>');
		            $('pdfList').add(fileTitle);
        			for(var i=0;i<pdflist.length;i++){
        				var pdfrealname = pdflist[i].pdfrealname+'';
	        			var pdfsavename = pdflist[i].pdfsavename+'';
	        			var pdfwjjmc = pdflist[i].pdfwjjmc+'';
	        			var pdfpzr = pdflist[i].pdfpzr+'';
	        			var pdfpzhj = pdflist[i].pdfpzhj+'';
	        			var pdfid = pdflist[i].pdfid+'';
	        			var pdfpzsj = pdflist[i].pdfpzsj+'';
	        			if(pdfpzsj!=''&& pdfpzsj!=null&&pdfpzsj.length>20){
	                        pdfpzsj = pdfpzsj.substring(0,16);
	                    }
	                    var pdfTitle ='';
	                    if(pdfpzsj==''){
	                    	pdfTitle = $C('<list-item><item style="col-width:30,*" onclick="qianPdf(\''+pdfrealname+
        						'\',\''+pdfsavename+'\',\''+pdfwjjmc+'\',\''+pdfpzr+'\',\''+pdfpzhj+'\',\''+pdfid+'\');">'+
        						'<col></col><col><label style="font-size:18;color:#38adff">'+pdfrealname+'</label>'+
	           					'</col></item></list-item>');
	                    }else{
	                    	pdfTitle = $C('<list-item><item style="col-width:30,*" onclick="qianPdf(\''+pdfrealname+
        						'\',\''+pdfsavename+'\',\''+pdfwjjmc+'\',\''+pdfpzr+'\',\''+pdfpzhj+'\',\''+pdfid+'\');">'+
        						'<col></col><col><label style="font-size:18;color:#38adff">'+pdfrealname+'</label>'+
	           					'<label style="font-size:12;color:#555555;">'+pdfpzsj+'</label>'+
	           					'<label style="font-size:12;color:#555555;">'+pdfpzr+'</label>'+
	           					'</col></item></list-item>');
	                    }
	           			$('pdfList').add(pdfTitle);
        			}
        			$('main_popupmenu').open();
        		}else if(pdflist instanceof Object){
        			var pdfdata = pdflist.pdfdata;
        			var pdfrealname = pdfdata.pdfrealname+'';
        			var pdfsavename = pdfdata.pdfsavename+'';
        			var pdfwjjmc = pdfdata.pdfwjjmc+'';
        			var pdfpzr = pdfdata.pdfpzr+'';
        			var pdfpzhj = pdfdata.pdfpzhj+'';
        			var pdfid = pdfdata.pdfid+'';
        			qianPdf(pdfrealname,pdfsavename,pdfwjjmc,pdfpzr,pdfpzhj,pdfid);
        		}
        	}

        	var pdfStatue='0';
        	function qianPdf(realName,saveName,path,pdfpzr,pdfpzhj,pdfid){
	            if(pdfStatue=='1'){
	            	return;
	            }
	            pdfStatue='1';
	            $("pdfid").value=pdfid;
	            $('main_popupmenu').close();
	            $http.post(evoUrl+'/ezOffice/doc/dealPdf.do',
	              {realName:realName,saveName:saveName,path:path},
	              function(data) {
		               if(data){
		                   var obj = JSON.parse(data);
		                   if(obj.logonerror &&obj.logonerror=='1'){
				         		alert("该账号已在设备"+obj.deviceId+"上登录");
			          			$page.open('../index.xml',{target:'_self'});
			                    return;
			               }
			               var result = obj.result;
			               if(result=="1"){ 
			                 	var saveName = obj.saveName;
			                 	$wps.openPdf(evoUrl+saveName,
					             {userName:un,isCanFieldEdit:true,isAnnotProtect:false,isUseEbenSDK:false,isSaveVector:true,isVectorsign:true,
					              isSaveHistory:true,isFillTemplate:false,pageViewMode:0},
					             {success:function(s){
					             	$("pdfatt").value = s;
									var time = getTime();
									$("pdfzhpzsj").value=time;
									var huajie = un+"("+time+""+workName+")";
									if(pdfpzr !=''){
										$("pdfpzr").value=pdfpzr+","+un;
										$("pdfpzhj").value=pdfpzhj+","+huajie;
									}else{
										$("pdfpzr").value=un;
										$("pdfpzhj").value=huajie;
									}
									alert("签批成功！");
					              },error:function(s){alert(s)}});
			               }
		               }
		            },function(error) {
						if (error == 'timeout') {
							hint('连接服务器超时！');
					    } else if (error == '404') {
							hint('链接超时重新登录！');
					    } else if (error == '500') {
							hint('内部服务器错误！');
					    } else {
							hint('访问网络错误！错误代码:' + error);
					    }
					    $page.open('../index.xml',{animation:'down-to-up'});
					});
					pdfStatue='0';
        	}

        	function getTime(){
				var myDate = new Date();
				myDate.getFullYear();
				var month = myDate.getMonth()+1;
				myDate.getDate(); 
				myDate.getHours();
				myDate.getMinutes();
				var nowDate = myDate.getFullYear()+'-'+month+'-'+myDate.getDate()+' '+myDate.getHours()+':'+myDate.getMinutes();
				return nowDate;
			}

