	        }

			// 需要创建附件下载内容的数组
	        var attachmentArray = new Array();

	        // 创建附件下载内容
	        function createAttachCon(){
	        	var attachments;
	        	attachmentArray.map(function(attachmentInfo){
	        		attachments = attachmentInfo.split(';');
					$http.post(evoUrl + '/ezOffice/doc/dealFiles.do', {
						accessoryNameseries : attachments[1].replace(/,/g,'|'),
						accessorySaveNameseries : attachments[0].replace(/,/g,'|'),
						path : attachments[3]
					}, function(data) {
						if (data) {
							var obj = JSON.parse(data);
							if(obj.logonerror &&obj.logonerror=='1'){
				         		alert("该账号已在设备"+obj.deviceId+"上登录");
			          			$page.open('../index.xml',{target:'_self'});
			                    return;
			                }
							var attList = obj.attList;
							var attachIdArr = attachmentInfo.split(';');  //解决异步提交时，会造成获取的attachIdArr不是请求的那个。后面的数组元素会覆盖前面的数组元素。
							if (!attList == '') { //不为空，遍历,并为附件赋值
								for (var k = 0; k < attList.length; k++) {
									var files = $C('<row><label style="color:#330000;color:blue;"  onclick="$http.download(\'' + evoUrl + '/ezOffice/download/file.do?url=' + attList[k].attachurlser + '&amp;filename=' + attList[k].savename +'&amp;viewName='+attList[k].realname+ '\')">' + attList[k].realname + '</label></row>')
									$(attachIdArr[2]).add(files);
								}
							} else { //无附件，或者后台处理异常
								alert('附件下载失败！');
							}
						}
					},function(error) {
				    if (error == 'timeout') {
						hint('连接服务器超时！');
				    } else if (error == '404') {
						hint('链接超时重新登录！');
				    } else if (error == '500') {
						hint('内部服务器错误！');
				    } else {
						hint('访问网络错误！错误代码:' + error);
				    }
				    $page.open('../index.xml',{animation:'down-to-up'});
				  });
	        	});
	        }

        	//初始化收文表单数据
	        function createFormData(govDocDetail,detailData,workInfoData,commentAttitudeTypeSet){
	            var commentList = workInfoData.commentList;   //已办审批意见列表
	            var workcurstep = workInfoData.workInfo.workcurstep;    //当前审批意见名称
	            for(var key in govDocDetail) {
		       	 	if(key=='title'&&govDocDetail.title){
		        		itemContent += '<item style="col-width:100,*;padding:0;margin:0;"><col style="background:#f9f9f9;margin:10;padding:10;"><row><label style="align:left;font-size:16;color:#555555">'+govDocDetail.title.name+':</label>'+
		        				   '</row></col><col style="margin:10;padding:10;"><row><label style="color:#555555">'+(govDocDetail.title.text == undefined ? '' : govDocDetail.title.text)+'</label></row></col></item>';
		        	}
		        	if(key=='zjkySeq'&&govDocDetail.zjkySeq){
		        		itemContent += '<item style="col-width:100,*;padding:0;margin:0;"><col style="background:#f9f9f9;margin:10;padding:10;"><row><label style="align:left;font-size:16;color:#555555">'+govDocDetail.zjkySeq.name+':</label>'+
		        				   '</row></col><col style="margin:10;padding:10;"><row><label style="color:#555555">'+(govDocDetail.zjkySeq.text == undefined ? '' : govDocDetail.zjkySeq.text)+'</label></row></col></item>';
		        	}
		        	if(key=='fileUnit'&&govDocDetail.fileUnit){
		        		itemContent += '<item style="col-width:100,*;padding:0;margin:0;"><col style="background:#f9f9f9;margin:10;padding:10;"><row><label style="align:left;font-size:16;color:#555555">'+govDocDetail.fileUnit.name+':</label>'+
		        				   '</row></col><col style="margin:10;padding:10;"><row><label style="color:#555555">'+(govDocDetail.fileUnit.text == undefined ? '' : govDocDetail.fileUnit.text)+'</label></row></col></item>';
		        	}
		        	if(key=='fileNumber'&&govDocDetail.fileNumber){
		        		itemContent += '<item style="col-width:100,*;padding:0;margin:0;"><col style="background:#f9f9f9;margin:10;padding:10;"><row><label style="align:left;font-size:16;color:#555555">'+govDocDetail.fileNumber.name+':</label>'+
		        				   '</row></col><col style="margin:10;padding:10;"><row><label style="color:#555555">'+(govDocDetail.fileNumber.text == undefined ? '' : govDocDetail.fileNumber.text)+'</label></row></col></item>';
