	        // 构建自定义字段内容
			function createCustemFieldCon(custemField,cusIndex){
				var custemCon = '<item style="col-width:100,*;padding:0;margin:0;"><col style="background:#f9f9f9;margin:10;padding:10;">';
				custemCon += '<row><label style="align:left;font-size:16;color:#555555">'+custemField.name+':</label></row></col><col style="margin:10;padding:10;" id="cusFile_'+cusIndex+'">';
				var content = custemField.content;
				var fieldDisplayType = custemField.displayType;
				if(fieldDisplayType=='705' || fieldDisplayType=='704' || fieldDisplayType=='214' 
				    || fieldDisplayType=='212' || fieldDisplayType=='211' || fieldDisplayType=='210'){
				    if(content){
				    	var cons = content.split(";");
				    	custemCon += '<row><label style="color:#555555">'+cons[0]+'</label></row>';
				    }

				}else if(fieldDisplayType=='115'){   //附件
				    if(content&&content.length>0){
				   // 	var cons = content.split(";");
				    	attachmentArray.push(content+';cusFile_'+cusIndex+';customform');
				   // 	custemCon += '<row><label style="color:#555555">'+cons[0]+'</label></row>';
				    }
				}else{
					if(content && fieldDisplayType){
						custemCon += '<row><label style="color:#555555">'+content+'</label></row>';
					}
				}
				custemCon += '</col></item>';
				return custemCon;
			}

			// 打开手写签批或语言签批菜单
			function openCommentMenu(){
				var $commentAdd = $('commentAdd');
				if($commentAdd.src == 'deskperson_add.png'){
					$('commentAdd').src = 'workflow_del.png';
					$('shouxie').css('display','');
					$('yuyin').css('display','');
				}else if($commentAdd.src == 'workflow_del.png'){
					$('commentAdd').src = 'deskperson_add.png';
					$('shouxie').css('display','none');
					$('yuyin').css('display','none');
				}
			}

			var drfNum = '0';
			//手写审批
			function doDraft() {
				if(drfNum == "1"){
					alert("不能重复签批！");
					return;
				}
		        $page.graffiti({
		        	type: 'draft',
		            complete: function(path){
		        		$('comment_input_shouxie').value = path;
		        		$('commentType').value = "4";
						var row = $C('<row id="sxrow" style="background:#ffffff"><icon src="'+path+'" style="width:150;height:80;"/><icon id="imageId" src="mail_del.png" style="width:30;height:30;"/></row>');
						$('comment_input_sxyy').add(row);
