				refreshOther('1');
		   }

			function add(){
			    //0周报 1月报 2其它
				if(flag == 0){
				    storage.setItem('type','my');
					$page.open('reweek_new.xml', {animation:'down-to-up'});
				}else if(flag == 1){
				    storage.setItem('type','my');
					$page.open('remonth_new.xml', {animation:'down-to-up'});
				}else{
				    storage.setItem('type','my');
					$page.open('reother_new.xml', {animation:'down-to-up'});
				}
			}
